import { v4 as uuidv4 } from 'uuid';

const initialState = [
    { id: 1, name: 'gym', duration: 1 },
    { id:2, name: 'running', duration: 0.5}
];

const actReducer = (state = initialState, action) => {
    const { type, payload } = action;

    switch (type) {
        case "CREATE":
            return [...state, {id: uuidv4(), }]
            break;
    
        default:
            break;
    }


    return state;
}
export default actReducer;
